import { useState, useEffect, useRef } from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import { useTranslation } from "react-i18next";

import { SelectButton } from "./SelectButton";
import {
  convertNumberToPersian,
  formatNumber,
  useWindowSize,
} from "../lib/utils";
import { Donation } from "../types";
import { DonationList } from "./DonationList";
import { BASE_PATH } from "../lib/constants";

const BTC_ADDRESS = "bc1qesms3zcc4um0m3mv94jrmtjnfd98jucm29d7yh";
const ETH_ADDRESS = "0xCEee44eF4BD8A2B7af0917EC517449f32E448a65";

export const DonationView = () => {
  const { t, i18n } = useTranslation(undefined, { keyPrefix: "donation" });
  const [donations, setDonations] = useState<Donation[]>([]);
  const [sortBy, setSortBy] = useState<"amount" | "date">("amount");
  const [btcPaymentShow, setBtcPaymentShow] = useState(false);
  const [ethPaymentShow, setEthPaymentShow] = useState(false);
  const _ = useWindowSize();
  const totalAmount = donations.reduce((pv, cv) => pv + cv.amount, 0);
  const formattedTotalAmount = formatNumber(totalAmount, "Ù¬");
  const sortedDonations =
    sortBy === "amount"
      ? donations.sort((a, b) => (a.amount < b.amount ? 1 : -1))
      : donations.sort((a, b) => (a.date > b.date ? 1 : -1));

  useEffect(() => {
    const fetchDonations = async () => {
      const response = await fetch(`${BASE_PATH}/donations.json`);
      setDonations(await response.json());
    };
    fetchDonations();
  }, []);

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        textAlign: "center",
        fontSize: "0.8rem",
        width: "100%",
      }}
    >
      <p>{t("payment_title")}</p>
      <Box
        sx={{
          display: "flex",
          gap: 1,
          flexWrap: "wrap",
          justifyContent: "center",
          my: 2,
          marginTop: 0,
        }}
      >
        <Button
          color="primary"
          variant="outlined"
          onClick={() => {
            setEthPaymentShow(false);
            setBtcPaymentShow(!btcPaymentShow);
          }}
          sx={{ borderRadius: "25px" }}
        >
          Bitcoin BTC
        </Button>
        <Button
          color="primary"
          variant="outlined"
          onClick={() => {
            setBtcPaymentShow(false);
            setEthPaymentShow(!ethPaymentShow);
          }}
          sx={{ borderRadius: "25px" }}
        >
          Ethereum ETH
        </Button>
      </Box>
      <Box
        id="btc-address"
        sx={{ display: btcPaymentShow ? "block" : "none", mb: 3 }}
      >
        <div>
          <b>Bitcoin</b>
        </div>
        <div>
          <a href={`bitcoin:${BTC_ADDRESS}`}>
            <img src="data:image/png;base64," />
          </a>
        </div>
        <Box
          sx={(theme: any) => ({
            display: "inline-block",
            border: 1,
            borderColor: theme.palette.devider,
            p: 0.5,
          })}
          onClick={() => {
            navigator.clipboard.writeText(BTC_ADDRESS);
            alert("Copied");
          }}
        >
          {BTC_ADDRESS}
        </Box>
      </Box>
      <Box
        id="eth-address"
        sx={{ display: ethPaymentShow ? "block" : "none", mb: 3 }}
      >
        <div>
          <b>Ethereum</b>
        </div>
        <div>
          <a href={`ethereum:${ETH_ADDRESS}`}>
            <img src="data:image/png;base64," />
          </a>
        </div>
        <Box
          sx={(theme: any) => ({
            display: "inline-block",
            border: 1,
            borderColor: theme.palette.devider,
            p: 0.5,
          })}
          onClick={() => {
            navigator.clipboard.writeText(ETH_ADDRESS);
            alert("Copied");
          }}
        >
          {ETH_ADDRESS}
        </Box>
      </Box>
      <Box
        sx={{
          display: "flex",
          columnGap: 3,
          rowGap: 1,
          alignItems: "center",
          justifyContent: "center",
          flexWrap: "wrap",
        }}
      >
        <Box sx={{ display: "flex", alignItems: "center" }}>
          {t("donations_from")}:{" "}
          <span style={{ direction: "ltr", margin: "0 5px" }}>
            {`${formattedTotalAmount} USD`}
          </span>
        </Box>
        <Box
          sx={{
            display: donations.length > 0 ? "flex" : "none",
            justifyContent: "center",
            alignItems: "center",
            gap: 1,
          }}
        >
          {t("order_by")}:
          <SelectButton
            color="inherit"
            variant="text"
            onClick={() => setSortBy("date")}
            selected={sortBy === "date"}
            size="small"
            sx={{ fontSize: "0.8rem", minWidth: "unset" }}
          >
            {t("date")}
          </SelectButton>
          <SelectButton
            color="inherit"
            variant="text"
            onClick={() => setSortBy("amount")}
            selected={sortBy === "amount"}
            size="small"
            sx={{ fontSize: "0.8rem", minWidth: "unset" }}
          >
            {t("amount")}
          </SelectButton>
        </Box>
      </Box>
      {donations.length === 0 && t("fetching_donations")}
      {donations.length > 0 && <DonationList donations={sortedDonations} />}
      <Box mt={1} sx={{ display: donations.length > 0 ? "block" : "none" }}>
        <a
          href="https://github.com/nadalaba/vazirharf/blob/gh-pages/donations.json"
          target="_blank"
          rel="noreferrer"
        >
          {t("list_source")}
        </a>
      </Box>
    </Box>
  );
};
